fetch("api/teddies").then(data=>data.json()).then(teddies=>teddies.map(teddy=>{const mainContainer=document.getElementById("myData"),columnContainer=document.createElement("div");columnContainer.setAttribute("class","col-md-4"),mainContainer.appendChild(columnContainer);const cardContainer=document.createElement("figure");cardContainer.setAttribute("class","card card-product"),columnContainer.appendChild(cardContainer);const imageWrap=document.createElement("div");imageWrap.setAttribute("class","img-wrap"),cardContainer.appendChild(imageWrap);const imageContainer=document.createElement("img");imageContainer.setAttribute("src",teddy.imageUrl),imageWrap.appendChild(imageContainer);const infoWrap=document.createElement("figcaption");infoWrap.setAttribute("class","info-wrap"),cardContainer.appendChild(infoWrap);const teddyName=document.createElement("h4");teddyName.setAttribute("class","title"),teddyName.innerHTML=teddy.name,infoWrap.appendChild(teddyName);const teddyDescription=document.createElement("p");teddyDescription.setAttribute("class","desc"),teddyDescription.innerHTML=teddy.description,infoWrap.appendChild(teddyDescription)}));